+void _showEditMessageDialog(String messageId, String oldText) {
+  final TextEditingController _editController = TextEditingController(text: oldText);

+  showDialog(
+    context: context,
+    builder: (context) {
+      return AlertDialog(
+        title: const Text("Edit Message"),
+        content: TextField(
+          controller: _editController,
+          maxLines: null,
+          decoration: const InputDecoration(hintText: "Enter new message"),
+        ),
+        actions: [
+          TextButton(
+            onPressed: () => Navigator.pop(context),
+            child: const Text("Cancel"),
+          ),
+          TextButton(
+            onPressed: () async {
+              String updatedText = _editController.text.trim();
+              if (updatedText.isNotEmpty) {
+                await FirebaseFirestore.instance
+                    .collection('chats')
+                    .doc(messageId)
+                    .update({'message': updatedText});
+              }
+              Navigator.pop(context);
+            },
+            child: const Text("Save"),
+          ),
+        ],
+      );
+    },
+  );
+}

