import 'package:flutter/material.dart';
import 'package:l_f/Frontend/Contants/lists.dart'; // Adjust this import

class FilterBar extends StatefulWidget {
  final Function({String? type, String? location, String? category, String? dateRange}) onFilterChanged;

  const FilterBar({super.key, required this.onFilterChanged});

  @override
  State<FilterBar> createState() => _FilterBarState();
}

class _FilterBarState extends State<FilterBar> {
  String selectedType = 'All';
  String selectedLocation = 'Campus, NITH';
  String selectedCategory = 'All';
  String selectedDateRange = 'All Time';

  final List<String> itemTypes = ['All', 'Lost', 'Found'];
  final List<String> locations = locationsList;
  final List<String> categories = ['All', 'Mobile Phone', 'Laptop', 'Other']; // Add your full list
  final List<String> dateRanges = ['All Time', 'Today', 'This Week', 'This Month', 'This Year'];

  @override
  Widget build(BuildContext context) {
    return SingleChildScrollView(
      scrollDirection: Axis.horizontal,
      padding: const EdgeInsets.symmetric(horizontal: 12.0, vertical: 8.0),
      child: Row(
        mainAxisAlignment: MainAxisAlignment.center,
        children: [
          _buildDropdown('Type', selectedType, itemTypes, (val) => widget.onFilterChanged(type: val)),
          _buildDropdown('Location', selectedLocation, locations, (val) => widget.onFilterChanged(location: val)),
          _buildDropdown('Category', selectedCategory, categories, (val) => widget.onFilterChanged(category: val)),
          _buildDropdown('Date', selectedDateRange, dateRanges, (val) => widget.onFilterChanged(dateRange: val)),
        ],
      ),
    );
  }

  Widget _buildDropdown(String hint, String value, List<String> items, ValueChanged<String?> onChanged) {
    return Padding(
      padding: const EdgeInsets.symmetric(horizontal: 8.0),
      child: DropdownButtonHideUnderline(
        child: DropdownButton<String>(
          value: value,
          hint: Text(hint),
          items: items.map((String val) => DropdownMenuItem<String>(value: val, child: Text(val))).toList(),
          onChanged: onChanged,
        ),
      ),
    );
  }
}
