import 'package:flutter/material.dart';
import 'package:shimmer/shimmer.dart';

class PostShimmerCard extends StatelessWidget {
  const PostShimmerCard({super.key});

  @override
  Widget build(BuildContext context) {
    return Shimmer.fromColors(
      baseColor: Colors.grey[300]!,
      highlightColor: Colors.grey[100]!,
      child: Card(
        elevation: 4,
        margin: const EdgeInsets.symmetric(vertical: 8.0),
        shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(12)),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            ListTile(
              leading: const CircleAvatar(radius: 25, backgroundColor: Colors.white),
              title: Container(width: 120.0, height: 16.0, color: Colors.white),
              subtitle: Container(width: 200.0, height: 12.0, color: Colors.white, margin: const EdgeInsets.only(top: 4)),
            ),
            Container(
              height: 200.0,
              color: Colors.white,
            ),
            Padding(
              padding: const EdgeInsets.all(16.0),
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  Container(width: double.infinity, height: 20.0, color: Colors.white),
                  const SizedBox(height: 8),
                  Container(width: 150.0, height: 12.0, color: Colors.white),
                  const SizedBox(height: 12),
                  Container(width: 250.0, height: 14.0, color: Colors.white),
                ],
              ),
            ),
          ],
        ),
      ),
    );
  }
}
