// lib/Frontend/components/filter_dropdowns.dart
import 'package:flutter/material.dart';
import 'package:l_f/Frontend/Contants/lists.dart';

class FilterDropdowns extends StatelessWidget {
  final String selectedType;
  final String selectedLocation;
  final String selectedCategory;
  final String selectedDateRange;
  final Function(String?) onTypeChanged;
  final Function(String?) onLocationChanged;
  final Function(String?) onCategoryChanged;
  final Function(String?) onDateRangeChanged;

  const FilterDropdowns({
    super.key,
    required this.selectedType,
    required this.selectedLocation,
    required this.selectedCategory,
    required this.selectedDateRange,
    required this.onTypeChanged,
    required this.onLocationChanged,
    required this.onCategoryChanged,
    required this.onDateRangeChanged,
  });

  Widget _buildDropdown(String selectedValue, List<String> items, ValueChanged<String?> onChanged) {
    return DropdownButton<String>(
      value: selectedValue,
      items: items.map((String value) {
        return DropdownMenuItem<String>(
          value: value,
          child: Text(value),
        );
      }).toList(),
      onChanged: onChanged,
    );
  }

  @override
  Widget build(BuildContext context) {
    return SingleChildScrollView(
      scrollDirection: Axis.horizontal,
      child: Padding(
        padding: const EdgeInsets.symmetric(horizontal: 12.0),
        child: Row(
          mainAxisAlignment: MainAxisAlignment.center,
          mainAxisSize: MainAxisSize.min,
          children: [
            _buildDropdown(selectedType, ['All', 'Lost', 'Found'], onTypeChanged),
            const SizedBox(width: 10),
            _buildDropdown(selectedLocation, locationsList, onLocationChanged),
            const SizedBox(width: 10),
            _buildDropdown(selectedCategory, itemsList, onCategoryChanged), // Using itemsList from your constants
            const SizedBox(width: 10),
            _buildDropdown(selectedDateRange, ['All Time', 'Today', 'This Week', 'This Month', 'This Year'], onDateRangeChanged),
          ],
        ),
      ),
    );
  }
}
