// lib/Frontend/pages/home_screen.dart
import 'package:flutter/material.dart';
import 'package:l_f/Frontend/components/custom_app_bar.dart';
import 'package:l_f/Frontend/components/custom_drawer.dart';
import 'package:l_f/Frontend/Home/home.dart';
import 'package:l_f/Frontend/Home/Post/create_post.dart';
import 'package:l_f/Frontend/Profile/profile_page.dart';

class HomePage extends StatelessWidget {
  const HomePage({super.key});

  @override
  Widget build(BuildContext context) {
    return LayoutBuilder(
      builder: (context, constraints) {
        bool isMobile = constraints.maxWidth < 700;
        final GlobalKey<ScaffoldState> _scaffoldKey = GlobalKey<ScaffoldState>();

        return Scaffold(
          key: _scaffoldKey,
          appBar: CustomAppBar(isMobile: isMobile, scaffoldKey: _scaffoldKey),
          drawer: isMobile ? const CustomDrawer() : null,
          body: isMobile
              ? const Center(child: LostFoundPage())
              : const Column(
                  children: [
                    Expanded(
                      flex: 5,
                      child: Center(
                        child: LostFoundPage(),
                      ),
                    ),
                  ],
                ),
        );
      },
    );
  }
}
